    if (isNaN(totalValueNow))
        totalValueNow = 0;
    var action, totalVal;
    if ($.inArray(parseInt(productId), comparedProductsIds) === -1 && $.inArray('' + productId, comparedProductsIds) === -1)
        action = 'add';
